import React, { useEffect, useState } from 'react';
import { Button, StyleSheet, Text, TouchableOpacity, View } from 'react-native';
import { FlatList } from 'react-native-gesture-handler';
import {panelStyles, viewingStyles} from '../styles';
import Entity from './PartyListEntity';
import {connect} from 'react-redux';
import {getParties} from './../API/PartyApi';
export default function Party({navigation, route}) {
  const [list, setList] = useState([]);
  const [loaded, setLoaded] = useState(false);
  useEffect(()=>{
    if(!loaded)
      route.params.getList((data)=>{ 
        setList(data);
        setLoaded(true);
      });
  },[list, setList])
  console.log(list, "party");
  return ( 
    <View>
      {list? 
        <FlatList
          data={list.slice(0, list.length)}
          renderItem={(item)=><Entity {...item} navigation={navigation}/>}
        >
            
        </FlatList>:""}
    </View>
  );
}
